import React, { Component } from 'react';
import './form.css'
export default class Formulario extends Component {
    constructor(){
        super()
        this.titulo = '';
        this.nota = '';

    }
    handleMudancaTitulo(evento){
        this.titulo = evento.target.value
    }
    handleMudancaNota(evento){
        this.nota = evento.target.value
    }
    render() {
        return (
            <section className='border'>
                <form className='Form-cadastro'>
                    <input onChange={this.handleMudancaTitulo} className='Titulo-input' type="text" placeholder="TÃ­tulo" />
                    <textarea onChange={this.handleMudancaNota} placeholder="Escreva sua nota..."></textarea>
                    <button>Criar Nota</button>
                </form>
            </section>
        );
    }
}
